//to get context sensitive editor run:
log.info testRunner.metaClass.methods*.name.unique().sort()
//in log, available methods listed:
//[cancel, equals, fail, getClass, getLog, getReason, getResults, getRunContext, getStartTime, 
//getStatus, getTestCase, getTestRunnable, getTimeTaken, gotoStep, gotoStepByName, hashCode, 
//isRunning, notify, notifyAll, runTestStep, runTestStepByName, setMockRunContext, setRunContext, start, 
//toString, wait, waitUntilFinished]

//global variable: testRunner, Context, log

class Hello{
//must define these three
	def log;
	def context;
	def testRunner;

	//constructor
	def Hello(log, context, testRunner) {
		 //this-> above defined with def log;this.log 
		 this.log = log  //passed in Hello constructor
		 this.context = context
		 this.testRunner = testRunner
	}
	def sayHello(String name){
		log.info ("    Hello "+name+"  dear");
	}
}
context.setProperty("Hello", new Hello(log,context,testRunner))
//must run this "new" instantiation at the end of class

def testStep = testRunner.testCase.getTestStepByName("Hello")

testStep.run(testRunner, context)
//now all methods of Hello class can be called
def name = "Władysław"
context.Hello.sayHello(name)

//to get to method in a class outside test case, must navigate as below
project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["TestSuite2"].testCases["TestCase1"]; //test case name
hello = tcase.getTestStepByName("Hello");

def testStep = testRunner.testCase.getTestStepByName("Hello")

hello.run(testRunner, context)
//now all methods of Hello class can be called
def name = "Stanisław"
context.Hello.sayHello(name)

//Groovy - run request from the same TestCase i.e. TestCase3.  
